    private ResourcesBO findByCache(String contentCode, String clientType, String gender, int page, int limit, String app_version) {

        final String level1CacheKey = "yh:resources:" + contentCode + clientType + gender + page + limit + app_version;
        final String level2CacheKey = "yh:resources:l2:" + contentCode + clientType + gender + page + limit + app_version;

        //find by cache first
        logger.info("begin level1 cache  for home.resource by content code:{}  from level1Cache. level1CacheKey:{}", contentCode, level1CacheKey);
        ResourcesBO bo = this.holder.getLevel1Cache().get(level1CacheKey, ResourcesBO.class);
        if (bo != null) {
            logger.info("cache hit for home.resource by content code:{}  from level1Cache.", contentCode);
            return bo;
        }
        //find by service call
        ResourcesRequestBody request = new ResourcesRequestBody();
        request.setContentCode(contentCode);
        request.setClientType(clientType);
        request.setGender(gender);
        request.setPage(page);
        request.setLimit(limit);
        request.setAppVersion(app_version);
        try {
            logger.info("begin find serviceCaller home.resource by content code: {} client type:{} gender:{} page:{} limit:{}", contentCode, clientType, gender, page, limit);
            bo = serviceCaller.call(ResourcesServices.home, request, ResourcesBO.class);
            logger.info("find serviceCaller home.resource success by content code: {} client type:{} gender:{} page:{} limit:{}", contentCode, clientType, gender, page, limit);
            if (bo != null) {
                this.holder.getLevel1Cache().set(level1CacheKey, ResourcesCacheExpireTime.RESOURCES_HOME, bo);
                this.holder.getLevel2Cache().set(level2CacheKey, level2Expire, bo);
            }
            logger.info("refresh resource.home success. cacheKey:{} content code: {} client type:{} gender:{} page:{} limit:{}", level1CacheKey, contentCode, clientType, gender, page, limit);
            return bo;
        } catch (Exception e) {
            //find by cache level2
            bo = this.holder.getLevel2Cache().get(level2CacheKey, ResourcesBO.class);
            if (bo != null) {
                // 将二级缓存中的数据放入到一级缓存中缓存1分钟
                this.holder.getLevel1Cache().set(level1CacheKey, 60, bo);
                logger.info("cache hit for home.resource by content code:{} from level2Cache. level2CacheKey:{}", contentCode, level2CacheKey);
            } else {
                logger.info("can not find home.resource by content code: {} client type:{} gender:{} page:{} limit:{}", contentCode, clientType, gender, page, limit);
            }
            return bo;
        }
    }


    /**
     * 清除需要忽略的资源
     *
     * @param uid
     * @param data
     */
    private void clearNeedIgnoreResources(String isNewDevice, int uid, ResourcesBO data) {
        if (data == null || data.getList() == null) {
            return;
        }
        // 如果是不是新人去除<新人专享>模块
        if (!isNewUser(isNewDevice, uid)) {
            for (Object obj : data.getList()) {
                if (obj instanceof JSONObject) {
                    JSONObject resource = (JSONObject) obj;
                    if ("new_user_floor".equals(resource.getString("template_name"))) {
                        resource.clear();
                    }
                }
            }
        }
    }


    /**
     * 判断是否新用户,默认老用户
     */
    @RequestMapping(params = "method=app.resources.isNewUser")
    @ResponseBody
    public ApiResponse decideIsNewUser(@RequestParam(value = "new_device", required = false, defaultValue = "N") String isNewDevice,
                                       @RequestParam(value = "uid", required = false, defaultValue = "0") int uid) {
        return new ApiResponse.ApiResponseBuilder().code(200).message("是否新用户").data(isNewUser(isNewDevice, uid)).build();
    }

    /**
     * 判断是否新用户,默认老用户
     *
     * @return
     */
    private boolean isNewUser(String isNewDevice, int uid) {
        // 用户未登录且存在设备唯一编号
        if (uid == 0) {
            return "Y".equalsIgnoreCase(isNewDevice);
        } else {
            String level1CacheKey = "yh:resources:is_new_user:" + uid;
            String level2CacheKey = "yh:resources:l2:is_new_user:" + uid;
            Boolean newUser = this.holder.getLevel1Cache().get(level1CacheKey, Boolean.class);
            if (newUser == null) {
                try {
                    newUser = isNewUserFromYHService(uid);
                    this.holder.getLevel1Cache().set(level1CacheKey, ResourcesCacheExpireTime.RESOURCES_IS_NEW_USER, newUser);
                    this.holder.getLevel2Cache().set(level2CacheKey, level2Expire, newUser);
                } catch (Exception e) {
                    //find by cache level2
                    newUser = this.holder.getLevel2Cache().get(level2CacheKey, Boolean.class);
                    if (newUser != null) {
                        // 将二级缓存中的数据放入到一级缓存中缓存1分钟
                        this.holder.getLevel1Cache().set(level1CacheKey, 60, newUser);
                    }
                }

            }
            return newUser == null ? false : newUser;
        }
    }

    private Boolean isNewUserFromYHService(int uid) {
        OrdersStatusStatisticsRequest request = new OrdersStatusStatisticsRequest();
        request.setUid(uid);
        CountBO countBO = serviceCaller.asyncCall(OrderServices.getOrdersCountByUidAndStatus, request, CountBO.class, defaultBO()).get(1);
        if (countBO.getCount() == 0) {
            return true;
        } else {
            logger.info("User {} is an old user, because he has {} orders.", uid, countBO.getCount());
            return false;
        }
    }

    private CountBO defaultBO() {
        CountBO bo = new CountBO();
        bo.setCount(1);
        return bo;
    }

